import java.util.*;
public class Pro5_Multiplication {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Scanner s=new Scanner(System.in);
		int mul;
		System.out.println("Please Enter Two Numbers For Multiplication =\t");
		int a=s.nextInt();
		int b=s.nextInt();
		
		
		mul=a*b;
		System.out.println("Multiplication of two numbers is = \t"+mul);

	}

}
